import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';

const GenrePage = () => {
  const { id } = useParams();  // Get the genre ID from the URL
  const [songs, setSongs] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    console.log(`Fetching songs for genre ID: ${id}`);  // Log the genre ID being fetched
    // Fetch songs based on genre ID
    axios.get(`http://localhost:3000/api/songs/${id}`)  // Make sure this URL matches your backend
      .then((response) => {
        console.log('Fetched songs:', response.data);  // Log the response data
        setSongs(response.data);  // Store songs data in state
        setLoading(false);
      })
      .catch((error) => {
        console.error('Error fetching songs:', error);  // Log any errors in fetching
        setError('Failed to fetch songs');
        setLoading(false);
      });
  }, [id]);  // Dependency on genre ID, so it re-fetches when the genre changes

  console.log('Songs state:', songs);  // Log the current songs state after it's set

  if (loading) {
    console.log('Loading...');  // Log when loading state is true
    return <div>Loading songs...</div>;
  }

  if (error) {
    console.log('Error:', error);  // Log the error state
    return <div>{error}</div>;
  }

  return (
    <div className="genre-page">
      <h1>Songs for Genre</h1>
      <div className="songs-list">
        {songs.map((song, index) => {
          console.log('Rendering song:', song);  // Log each song before rendering
          return (
            <div key={index} className="song-item">
              <p>{song.title} - {song.artist}</p>
              
              {/* Display image */}
              <img 
                src={`http://localhost:3000${song.image}`} 
                alt={song.title} 
                style={{ width: '100px', height: '100px' }}  // Example styling
              />
              
              {/* Display audio */}
              <audio controls>
                <source src={`http://localhost:3000${song.file_path}`} type="audio/mp3" />
                Your browser does not support the audio element.
              </audio>
            </div>
          );
        })}
      </div>
    </div>
  );
};

export default GenrePage;
