import React, { useState, useEffect } from 'react';
import { useParams, Link } from 'react-router-dom';
import axios from 'axios';
import { FaPlay, FaPause, FaStepBackward, FaStepForward } from 'react-icons/fa';
import './Style/artistpage.css';

const ArtistPage = () => {
  const { artistName } = useParams();
  const [artist, setArtist] = useState(null);
  const [songs, setSongs] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [currentSong, setCurrentSong] = useState(null);
  const [player, setPlayer] = useState(null);
  const [isPlaying, setIsPlaying] = useState(false);
  const [currentTime, setCurrentTime] = useState(0);
  const [duration, setDuration] = useState(0);
  const [volume, setVolume] = useState(1);

  useEffect(() => {
    axios.get(`http://localhost:3000/api/artist/${artistName}`)
      .then((response) => {
        setArtist(response.data.artist);
        setSongs(response.data.songs);
        setLoading(false);
      })
      .catch(() => {
        setError('Failed to fetch artist info');
        setLoading(false);
      });
  }, [artistName]);

  const handlePlay = (song) => {
    if (player) {
      player.pause();
    }
    const newPlayer = new Audio(song.file_path);

    newPlayer.onloadedmetadata = () => setDuration(newPlayer.duration);
    newPlayer.ontimeupdate = () => setCurrentTime(newPlayer.currentTime);

    newPlayer.volume = volume;
    newPlayer.play();

    setPlayer(newPlayer);
    setCurrentSong(song);
    setIsPlaying(true);
  };

  const handlePause = () => {
    if (player) {
      player.pause();
      setIsPlaying(false);
    }
  };

  const togglePlayPause = () => {
    if (player) {
      if (isPlaying) {
        handlePause();
      } else {
        player.play();
        setIsPlaying(true);
      }
    }
  };

  const handleNext = () => {
    if (!currentSong) return;
    const currentIndex = songs.findIndex(song => song.file_path === currentSong.file_path);
    const nextIndex = (currentIndex + 1) % songs.length;
    handlePlay(songs[nextIndex]);
  };

  const handlePrevious = () => {
    if (!currentSong) return;
    const currentIndex = songs.findIndex(song => song.file_path === currentSong.file_path);
    const prevIndex = (currentIndex - 1 + songs.length) % songs.length;
    handlePlay(songs[prevIndex]);
  };

  const handleVolumeChange = (event) => {
    const newVolume = parseFloat(event.target.value);
    setVolume(newVolume);
    if (player) {
      player.volume = newVolume;
    }
  };

  const formatTime = (time) => {
    if (isNaN(time)) return '0:00';
    const minutes = Math.floor(time / 60);
    const seconds = Math.floor(time % 60).toString().padStart(2, '0');
    return `${minutes}:${seconds}`;
  };

  if (loading) return <div className="loading">Loading artist info...</div>;
  if (error) return <div className="error">{error}</div>;

  return (
    <div className="artist-page">
      <aside className="sidebar">
        <h2>Artists</h2>
      </aside>
      <main className="main-content">
        <div className="artist-header">
          <img src={artist.image} alt={artist.name} className="artist-image" />
          <h1>{artist.name}</h1>
        </div>
        <div className="song-list">
          {songs.map((song, index) => (
            <div key={index} className="song-item">
              <img src={song.image} alt={song.title} className="song-cover" />
              <div className="song-info">
                <span className="song-title">{song.title}</span>
                <span className="song-album">{song.album}</span>
              </div>
              <div className="play-button" onClick={() => handlePlay(song)}>
                <FaPlay />
              </div>
            </div>
          ))}
        </div>
      </main>

      {currentSong && (
        <footer className="footer">
          <div className="current-song-info">
            <img src={currentSong.image} alt={currentSong.title} className="footer-cover" />
            <div>
              <p className="song-title">{currentSong.title}</p>
              <p className="artist">{currentSong.artist}</p>
            </div>
          </div>
          <div className="playback-controls">
            <button onClick={handlePrevious}><FaStepBackward /></button>
            <button onClick={togglePlayPause}>
              {isPlaying ? <FaPause /> : <FaPlay />}
            </button>
            <button onClick={handleNext}><FaStepForward /></button>
          </div>
          <div className="song-progress">
            <span>{formatTime(currentTime)}</span>
            <input 
              type="range" 
              min="0" 
              max={duration} 
              value={currentTime} 
              onChange={(e) => player && (player.currentTime = e.target.value)}
              className="progress-bar"
            />
            <span>{formatTime(duration)}</span>
          </div>
          <div className="volume-control">
            <input 
              type="range" 
              min="0" 
              max="1" 
              step="0.01" 
              value={volume} 
              onChange={handleVolumeChange} 
              className="volume-slider"
            />
          </div>
        </footer>
      )}
    </div>
  );
};

export default ArtistPage;
