import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import './Style/genrepage.css';

const GenrePage = () => {
  const { id } = useParams();  // Get the genre ID from the URL
  const [songs, setSongs] = useState([]);
  const [genreName, setGenreName] = useState('');
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    console.log(`Fetching songs and genre for genre ID: ${id}`);

    // Fetch songs and genre name based on genre ID
    axios.get(`http://localhost:3000/api/songs/${id}`)
      .then((response) => {
        console.log('Fetched songs:', response.data);  // Log the response data
        setSongs(response.data);  // Store songs data in state
        if (response.data.length > 0) {
          setGenreName(response.data[0].genre); // Set the genre name based on the first song
        }
        setLoading(false);
      })
      .catch((error) => {
        console.error('Error fetching songs:', error);  // Log any errors in fetching
        setError('Failed to fetch songs');
        setLoading(false);
      });
  }, [id]);  // Dependency on genre ID, so it re-fetches when the genre changes

  if (loading) {
    return <div>Loading songs...</div>;
  }

  if (error) {
    return <div>{error}</div>;
  }

  return (
    <div className="genre-page">
      <h1>{genreName}</h1>  {/* Dynamically display the genre name */}
      <div className="songs-list">
        {songs.map((song, index) => {
          return (
            <div key={index} className="song-item">
              <p>{song.title} - {song.artist}</p>
              <img src={`${song.image}`} alt={song.title} />
              <audio controls>
                <source src={`${song.file_path}`} type="audio/mp3" />
                Your browser does not support the audio element.
              </audio>
            </div>
          );
        })}
      </div>
    </div>
  );
};

export default GenrePage;
