import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import './Style/genrepage.css';

const GenrePage = () => {
  const { id } = useParams();
  const [songs, setSongs] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [currentSong, setCurrentSong] = useState(null); // Current song state

  useEffect(() => {
    axios.get(`http://localhost:3000/api/songs/${id}`)
      .then((response) => {
        setSongs(response.data);
        setLoading(false);
      })
      .catch((error) => {
        setError('Failed to fetch songs');
        setLoading(false);
      });
  }, [id]);

  const handlePlay = (song) => {
    setCurrentSong(song); // Set the song to be the current one
  };

  if (loading) {
    return <div className="loading">Loading songs...</div>;
  }

  if (error) {
    return <div className="error">{error}</div>;
  }

  return (
    <div className="genre-page">
      <h1>{songs[0]?.genre || 'Genre'}</h1> {/* Display genre name dynamically */}

      <div className="songs-list">
        {songs.map((song, index) => (
          <div key={index} className="song-item" onClick={() => handlePlay(song)}>
            <img src={`${song.image}`} alt={song.title} />
            <p>{song.title} - {song.artist}</p>
            <audio controls>
              <source src={`${song.file_path}`} type="audio/mp3" />
              Your browser does not support the audio element.
            </audio>
          </div>
        ))}
      </div>

      {/* Footer with the currently playing song */}
      {currentSong && (
        <div className="footer">
          <audio controls autoPlay>
            <source src={`${currentSong.file_path}`} type="audio/mp3" />
            Your browser does not support the audio element.
          </audio>
          <div className="current-song-info">
            <p className="song-title">{currentSong.title}</p>
            <p className="artist">{currentSong.artist}</p>
            <p className="duration">{currentSong.duration ? `${currentSong.duration}s` : 'Loading duration...'}</p>
          </div>
        </div>
      )}
    </div>
  );
};

export default GenrePage;
