import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Style/HomePage.css';

// Profile component to display each genre
const Profile = ({ genre, image }) => {
  console.log("Image Path:", image);  // Check the path of the image
  return (
    <div className="genre">
      <img src={image} alt={genre} />
      <p>{genre}</p>
    </div>
  );
};

// Genre component to fetch and display genres
export default function Genre() {
  const [genres, setGenres] = useState([]); // State to store genres data
  const [loading, setLoading] = useState(true); // State to handle loading
  const [error, setError] = useState(null); // State to handle error

  // Fetch genres from the backend
  useEffect(() => {
    axios.get('http://localhost:3000/api/genres')
      .then((response) => {
        console.log("Genres data fetched successfully", response.data); // Log when data is successfully fetched
        setGenres(response.data); // Set the genres data in the state
        setLoading(false); // Set loading to false when data is fetched
      })
      .catch((error) => {
        console.error('Error fetching genres:', error); // Log any errors that occur during the fetch
        setError('Failed to fetch genres'); // Set error state
        setLoading(false);
      });
  }, []); // Empty dependency array to run the effect once when the component mounts

  if (loading) {
    return (
      <div className="loading-container">
        <p>Loading genres...</p>
        <div className="spinner"></div> {/* Add CSS for a spinner */}
      </div>
    );
  }
  

  if (error) {
    return <div>{error}</div>; // Display the error message
  }
  return (
    <section>
      <h1>Genres</h1>
      <div className="genres">
        {genres.map((genre, index) => (
          <Profile key={index} genre={genre.name} image={genre.image} />
        ))}
      </div>
    </section>
  );
}
