import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import './Style/HomePage.css';

const Profile = ({ genre, image, id }) => {
  return (
    <div className="genre">
      <Link to={`/genre/${id}`}>  {/* When a genre is clicked, route to GenrePage with its ID */}
        <img src={image} alt={genre} />
        <p>{genre}</p>
      </Link>
    </div>
  );
};

export default function HomePage() {
  const [genres, setGenres] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    axios.get('http://localhost:3000/api/genres')
      .then((response) => {
        setGenres(response.data);
        setLoading(false);
      })
      .catch((error) => {
        setError('Failed to fetch genres');
        setLoading(false);
      });
  }, []);

  if (loading) {
    return <div>Loading genres...</div>;
  }

  if (error) {
    return <div>{error}</div>;
  }

  return (
    <section>
      <h1>Genres</h1>
      <div className="genres">
        {genres.map((genre) => (
          <Profile key={genre.id} genre={genre.name} image={genre.image} id={genre.id} />
        ))}
      </div>
    </section>
  );
}
