import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Style/HomePage.css';

// Profile component to display each Test
const Profile = ({ Test, image }) => {
  return (
    <div className="tests">
      <img src={image} alt={Test} />
      <p>{Test}</p>
    </div>
  );
};

// Test component to fetch and display Tests
export default function Test() {
  const [Tests, setTests] = useState([]); // State to store Tests data
  const [loading, setLoading] = useState(true); // State to handle loading
  const [error, setError] = useState(null); // State to handle error

  // Fetch Tests from the backend
  useEffect(() => {
    axios.get('http://localhost:3000/api/test')
      .then((response) => {
        console.log("Tests data fetched successfully", response.data); // Log when data is successfully fetched
        setTests(response.data); // Set the Tests data in the state
        setLoading(false); // Set loading to false when data is fetched
      })
      .catch((error) => {
        console.error('Error fetching Tests:', error); // Log any errors that occur during the fetch
        setError('Failed to fetch Tests'); // Set error state
        setLoading(false);
      });
  }, []); // Empty dependency array to run the effect once when the component mounts

  if (loading) {
    return (
      <div className="loading-container">
        <p>Loading Tests...</p>
        <div className="spinner"></div> {/* Add CSS for a spinner */}
      </div>
    );
  }
  

  if (error) {
    return <div>{error}</div>; // Display the error message
  }
  return (
    <section>
      <h1>Tests</h1>
      <div className="Tests">
        {Tests.map((Test, index) => (
          <Profile key={index} Test={Test.name} image={Test.image} />
        ))}
      </div>
    </section>
  );
}
