import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import { FaPlay, FaPause, FaVolumeUp, FaVolumeDown } from 'react-icons/fa';
import './Style/genrepage.css';

const GenrePage = () => {
  const { id } = useParams();
  const [songs, setSongs] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [currentSong, setCurrentSong] = useState(null);
  const [player, setPlayer] = useState(null);

  useEffect(() => {
    axios.get(`http://localhost:3000/api/songs/${id}`)
      .then((response) => {
        setSongs(response.data);
        setLoading(false);
      })
      .catch(() => {
        setError('Failed to fetch songs');
        setLoading(false);
      });
  }, [id]);

  const handlePlay = (song) => {
    if (player) {
      player.pause();
    }
    const newPlayer = new Audio(song.file_path);
    newPlayer.play();
    setPlayer(newPlayer);
    setCurrentSong(song);
  };

  const handlePause = () => {
    if (player) {
      player.pause();
    }
  };

  const handleVolumeChange = (amount) => {
    if (player) {
      player.volume = Math.min(Math.max(player.volume + amount, 0), 1);
    }
  };

  if (loading) return <div className="loading">Loading songs...</div>;
  if (error) return <div className="error">{error}</div>;

  return (
    <div className="genre-page">
      <aside className="sidebar">
      </aside>
      <main className="main-content">
        <h1>{songs[0]?.genre || 'Genre'}</h1>
        <div className="song-list">
          <div className="song-list-header">
            <span>Title</span>
            <span>Album</span>
            <span>Date Added</span>
          </div>
          {songs.map((song, index) => (
            <div key={index} className="song-item" onClick={() => handlePlay(song)}>
              <span>{song.title}</span>
              <span>{song.album}</span>
              <span>{song.dateAdded}</span>
            </div>
          ))}
        </div>
      </main>
      {currentSong && (
        <footer className="footer">
          <div className="current-song">
            <p>{currentSong.title} - {currentSong.artist}</p>
          </div>
          <div className="controls">
            <button onClick={() => handlePlay(currentSong)}><FaPlay /></button>
            <button onClick={handlePause}><FaPause /></button>
            <button onClick={() => handleVolumeChange(0.1)}><FaVolumeUp /></button>
            <button onClick={() => handleVolumeChange(-0.1)}><FaVolumeDown /></button>
          </div>
        </footer>
      )}
    </div>
  );
};

export default GenrePage;
