import React, { useState, useEffect } from 'react';
import { useParams, Link } from 'react-router-dom';
import axios from 'axios';
import { FaPlay, FaPause, FaStepBackward, FaStepForward } from 'react-icons/fa';
import './Style/genrepage.css';
import Playlist from '../../util/playlist';

const GenrePage = () => {
  const { id } = useParams();
  const [songs, setSongs] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [currentSong, setCurrentSong] = useState(null);
  const [player, setPlayer] = useState(null);
  const [currentTime, setCurrentTime] = useState(0);
  const [duration, setDuration] = useState(0);
  const [volume, setVolume] = useState(1);
  const [isPlaying, setIsPlaying] = useState(false);
  const [genreName, setGenreName] = useState('');

  // Form state
  const [newSong, setNewSong] = useState({
    title: '',
    album: '',
    image: '',
    artist: '',
    file_path: ''
  });

  useEffect(() => {
    axios.get(`http://localhost:3000/api/songs/${id}`)
      .then((response) => {
        const backendSongs = response.data;
        const genre = backendSongs[0]?.genre || '';
        setGenreName(genre);

        const playlist = new Playlist();
        const tempSongs = playlist.getGenreSongs(genre);

        setSongs([...backendSongs, ...tempSongs]);
        setLoading(false);
      })
      .catch((err) => {
        console.error('[GenrePage] Failed to fetch songs:', err);
        setError('Failed to fetch songs');
        setLoading(false);
      });
  }, [id]);

  const handlePlay = (song) => {
    if (player) player.pause();

    const newPlayer = new Audio(song.file_path);

    newPlayer.onloadedmetadata = () => setDuration(newPlayer.duration);
    newPlayer.ontimeupdate = () => setCurrentTime(newPlayer.currentTime);

    newPlayer.volume = volume;
    newPlayer.play();

    setPlayer(newPlayer);
    setCurrentSong(song);
    setIsPlaying(true);
  };

  const handlePause = () => {
    if (player) {
      player.pause();
      setIsPlaying(false);
    }
  };

  const togglePlayPause = () => {
    if (player) {
      isPlaying ? handlePause() : player.play();
      setIsPlaying(!isPlaying);
    }
  };

  const handleNext = () => {
    if (!currentSong) return;
    const currentIndex = songs.findIndex(song => song.file_path === currentSong.file_path);
    const nextIndex = (currentIndex + 1) % songs.length;
    handlePlay(songs[nextIndex]);
  };

  const handlePrevious = () => {
    if (!currentSong) return;
    const currentIndex = songs.findIndex(song => song.file_path === currentSong.file_path);
    const prevIndex = (currentIndex - 1 + songs.length) % songs.length;
    handlePlay(songs[prevIndex]);
  };

  const handleVolumeChange = (event) => {
    const newVolume = parseFloat(event.target.value);
    setVolume(newVolume);
    if (player) player.volume = newVolume;
  };

  const formatTime = (time) => {
    if (isNaN(time)) return '0:00';
    const minutes = Math.floor(time / 60);
    const seconds = Math.floor(time % 60).toString().padStart(2, '0');
    return `${minutes}:${seconds}`;
  };

  // New song input handlers
  const handleInputChange = (e) => {
    setNewSong({ ...newSong, [e.target.name]: e.target.value });
  };

  const handleAddSong = () => {
    if (!newSong.title || !genreName) {
      alert("Title and file path are required");
      return;
    }

    const playlist = new Playlist();
    const songToAdd = { ...newSong, genre: genreName };
    playlist.saveGenreSongs(genreName, songToAdd);

    setSongs(prev => [...prev, songToAdd]);
    setNewSong({ title: '', album: '', image: '', artist: '', file_path: '' });
  };

  if (loading) return <div className="loading">Loading songs...</div>;
  if (error) return <div className="error">{error}</div>;

  return (
    <div className="genre-page">
      <aside className="sidebar">
        <h2>Your Library</h2>
      </aside>

      <main className="main-content">
        <h1>{genreName || 'Genre'}</h1>

        {/* Add Song Form */}
        <div className="add-song-form">
          <h3>Add a Temporary Song</h3>
          <input
            name="title"
            placeholder="Title"
            value={newSong.title}
            onChange={handleInputChange}
          />
          <input
            name="album"
            placeholder="Album"
            value={newSong.album}
            onChange={handleInputChange}
          />
          <input
            name="artist"
            placeholder="Artist"
            value={newSong.artist}
            onChange={handleInputChange}
          />
          <input
            name="image"
            placeholder="Image URL"
            value={newSong.image}
            onChange={handleInputChange}
          />
          <input
            name="file_path"
            placeholder="Audio file URL"
            value={newSong.file_path}
            onChange={handleInputChange}
          />
          <button onClick={handleAddSong}>Add Song</button>
        </div>

        <div className="song-list">
          <div className="song-list-header">
            <span>Title</span>
          </div>
          {songs.map((song, index) => (
            <div key={index} className="song-item">
              <img src={song.image} alt={song.title} className="song-cover" />
              <div className="song-info">
                <span className="song-title">{song.title}</span>
                <Link to={`/artist/${song.artist}`} className="song-artist">
                  {song.artist}
                </Link>
              </div>
              <div className="play-button" onClick={() => handlePlay(song)}>
                <FaPlay />
              </div>
            </div>
          ))}
        </div>
      </main>

      {currentSong && (
        <footer className="footer">
          <div className="current-song-info">
            <img src={currentSong.image} alt={currentSong.title} className="footer-cover" />
            <div>
              <p className="song-title">{currentSong.title}</p>
              <p className="artist">{currentSong.artist}</p>
            </div>
          </div>
          <div className="playback-controls">
            <button onClick={handlePrevious}><FaStepBackward /></button>
            <button onClick={togglePlayPause}>
              {isPlaying ? <FaPause /> : <FaPlay />}
            </button>
            <button onClick={handleNext}><FaStepForward /></button>
          </div>
          <div className="song-progress">
            <span>{formatTime(currentTime)}</span>
            <input
              type="range"
              min="0"
              max={duration}
              value={currentTime}
              onChange={(e) => player && (player.currentTime = e.target.value)}
              className="progress-bar"
            />
            <span>{formatTime(duration)}</span>
          </div>
          <div className="volume-control">
            <input
              type="range"
              min="0"
              max="1"
              step="0.01"
              value={volume}
              onChange={handleVolumeChange}
              className="volume-slider"
            />
          </div>
        </footer>
      )}
    </div>
  );
};

export default GenrePage;
