import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Style/HomePage.css';

// Profile component
const Profile = ({ genre, image }) => {
  return (
    <div className="genre">
      <img src={image} alt={genre} />
      <p>{genre}</p>
    </div>
  );
};

// Genre component
export default function Genre() {
  const [genres, setGenres] = useState([]); // State to store genres data
  const [loading, setLoading] = useState(true); // State to handle loading

  // Fetch genres from the backend
  useEffect(() => {
    axios.get('http://localhost:5000/api/genres')
      .then((response) => {
        setGenres(response.data); // Set the genres data in the state
        setLoading(false); // Set loading to false when data is fetched
      })
      .catch((error) => {
        console.error('Error fetching genres:', error);
        setLoading(false);
      });
  }, []); // Empty dependency array to run the effect once when the component mounts

  if (loading) {
    return <div>Loading genres...</div>; // Show loading text while data is being fetched
  }

  return (
    <section>
      <h1>Genres</h1>
      <div className="genres">
        {genres.map((genre, index) => (
          <Profile key={index} genre={genre.name} image={genre.image} />
        ))}
      </div>
    </section>
  );
}
