import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";
import "./Style/artistpage.css";
import Playlist from "../../util/playlist";

const ArtistPage = () => {
  const { artistName } = useParams();
  const [songs, setSongs] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [artistDescription, setArtistDescription] = useState("");

  useEffect(() => {
    console.log(`Fetching data for artist: ${artistName}`);

    axios
      .get(`http://localhost:3000/api/artists/${artistName}`)
      .then((response) => {
        console.log("Received data from backend:", response.data);

        const { artist, songs } = response.data;

        if (!songs || songs.length === 0) {
          setError("No songs found for this artist.");
          setLoading(false);
          return;
        }

        setSongs(songs);
        setArtistDescription(artist.description || "No description available.");
        setLoading(false);
      })
      .catch((error) => {
        console.error("Error fetching artist data:", error);
        setError("Failed to fetch artist data");
        setLoading(false);
      });
  }, [artistName]);

  if (loading) return <div className="loading">Loading artist info...</div>;
  if (error) return <div className="error">{error}</div>;

  const artistImage =
    songs.length > 0 ? songs[0].image : "/default-artist.jpg";

  const handleAddToPlaylist = (song) => {
    const playlist = new Playlist();
    playlist.addSong(song);
    alert(`Added "${song.title}" to playlist`);
  };

  return (
    <div className="artist-page">
      <div className="artist-header">
        <img src={artistImage} alt={artistName} className="artist-image" />
        <h1>{artistName}</h1>
      </div>

      <div className="artist-description">
        <h3>Artist Description</h3>
        <p>{artistDescription}</p>
      </div>

      <div className="song-list">
        {songs.map((song, index) => (
          <div key={index} className="song-item">
            <img src={song.image} alt={song.title} className="song-cover" />
            <div className="song-info">
              <span className="song-title">{song.title}</span>
              <span className="song-album">{song.album || "Unknown Album"}</span>
            </div>
            <button onClick={() => handleAddToPlaylist(song)}>
              Add to Playlist
            </button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default ArtistPage;
