import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";
import "./Style/artistpage.css";

const ArtistPage = () => {
  const { artistName } = useParams(); // Get artist name from URL
  const [songs, setSongs] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    console.log(`Fetching data for artist: ${artistName}`); // Log when the request is made

    // Correct the URL to use /api/artists
    axios.get(`http://localhost:3000/api/artists/${artistName}`)
      .then((response) => {
        console.log('Received data from backend:', response.data); // Log the data received from the backend

        const allSongs = response.data;

        // Filter songs by artist name (although the backend should already do this)
        const artistSongs = allSongs.filter(song => song.artist === artistName);

        if (artistSongs.length === 0) {
          console.log('No songs found for this artist'); // Log if no songs are found
          setError("Artist not found");
        } else {
          setSongs(artistSongs);
        }

        setLoading(false);
      })
      .catch((error) => {
        console.error('Error fetching artist data:', error); // Log the error
        setError("Failed to fetch artist data");
        setLoading(false);
      });
  }, [artistName]);

  if (loading) return <div className="loading">Loading artist info...</div>;
  if (error) return <div className="error">{error}</div>;

  // Use the first song's image as the artist image (or default image)
  const artistImage = songs[0]?.image || "/default-artist.jpg";

  return (
    <div className="artist-page">
      <div className="artist-header">
        <img src={artistImage} alt={artistName} className="artist-image" />
        <h1>{artistName}</h1>
      </div>

      <div className="song-list">
        {songs.map((song, index) => (
          <div key={index} className="song-item">
            <img src={song.image} alt={song.title} className="song-cover" />
            <div className="song-info">
              <span className="song-title">{song.title}</span>
              <span className="song-album">{song.album}</span>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default ArtistPage;
